<?php /**  * RecentHistories is a Yii widget used to display a list of recent history  */ class Receipts extends CWidget {    private $_receipts;     public $displayLimit = 50;     public $contractId = null;     public function init()    {        $this->_receipts = Receipt::model()             ->findReceipts($this->displayLimit, $this->contractId);    }     public function getReceipts()    {        return $this->_receipts;    }     public function run()     {        $this->render('receipts');    }}