<?php /**  * RecentHistories is a Yii widget used to display a list of recent history  */ class RecentHistories extends CWidget {    private $_histories;     public $displayLimit = 5;     public $contractId = null;     public function init()    {        $this->_histories = History::model()             ->findRecentHistories($this->displayLimit, $this->contractId);    }     public function getRecentHistories()    {        return $this->_histories;    }     public function run()     {        $this->render('recentHistories');    }}