<ul>     <?php foreach($this->getReceipts() as $receipt): ?>    <div class="author">         <?php echo $receipt->creater->username; ?> 更新了合同收据 ＃<?php echo CHtml::link(CHtml::encode($receipt->id),                array('receipt/update', 'id'=>$receipt->id)); ?>    </div>     <div class="issue">    	<table>    	<tr>    	<td><?php echo $receipt->getAttributeLabel('receipts_date');?></td>    	<td><?php echo $receipt->receipts_date;?></td>    	</tr>    	<tr>    	<td><?php echo $receipt->getAttributeLabel('amount');?></td>    	<td><?php echo $receipt->amount;?></td>    	</tr>    	<tr>    	<td><?php echo $receipt->getAttributeLabel('code');?></td>    	<td><?php echo $receipt->code;?></td>    	</tr>    	<tr>    	<td><?php echo $receipt->getAttributeLabel('content');?></td>    	<td><?php echo $receipt->content;?></td>    	</tr>    	</table>    </div>     <?php endforeach; ?></ul>